import React, { useEffect, useState } from "react";

import "./styles.css";

export default function Banner() {
  const [{ sec, min, hours, days }, setDate] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setDate({ sec: 10, min: 10, hours: 10, days: 10 });
    }, 1000);
  }, []);
  return (
    <>
      <section className="countdown-container">
        <h1>{new Date(new Date().getFullYear() + 1, 1, 1)}</h1>
        <h1>ContDown For New Year</h1>

        <div className="countdown-timer-area">
          <div className="months-container timer-area">
            <p className="legend" id="months">
              10
            </p>
            <span>Months</span>
          </div>
          <div className="days-container timer-area">
            <p className="legend" id="days">
              15
            </p>
            <span>Days</span>
          </div>
          <div className="hours-container timer-area">
            <p className="legend" id="hours">
              15
            </p>
            <span>Hours</span>
          </div>
          <div className="minutes-container timer-area">
            <p className="legend" id="minutes">
              15
            </p>
            <span>Minutes</span>
          </div>
          <div className="seconds-container timer-area">
            <p className="legend" id="seconds">
              15
            </p>
            <span>Seconds</span>
          </div>
        </div>
      </section>
    </>
  );
}
